#include "stdafx.h"#include <iostream>#include <string>using namespace std;class Book{private:	string name;	string author;	string publisher;	int pages;public:	Book()	{		name = "默认名字";		author = "默认作者";		publisher = "默认出版社";		pages = 0;	}	Book(string na, string aut, string pub, int pag)	{		name = na;		author = aut;		publisher = pub;		pages = pag;	}	void SetInfo(string na, string aut, string pub, int pag)	{		name = na;		author = aut;		publisher = pub;		pages = pag;	}	~Book()	{		cout << "Destruct the book" << name << endl;	}	void print()	{		cout << "name=" << name << endl << "author=" << author << endl << "publisher=" << publisher << endl << "pages=" << pages << endl;	}};int main(){	Book c1;	Book c2("houlai", "jiji", "cjn", 50);	c1.print();	c2.print();	return 0;};